mapper.select:
select a.*,b.loginname,cc.stuname
from blacklist a
left join sysuser b on b.id=a.userid
left join studentinfo cc on cc.userid=b.id
where 1=1

   and b.loginname=#{loginname}


  and cc.stuname=#{stuname}


  order by ${orderby}


  limit ${beginRow},${pageSize}

select
count(id) as total
from blacklist
where 1=1

  and b.loginname=#{loginname}


  and cc.stuname=#{stuname}

select a.*,b.loginname,cc.stuname,d.loanpurpose,e.stagenum,e.repaymoney,e.repaydateplan
from blacklist a
left join sysuser b on b.id=a.userid
left join studentinfo cc on cc.userid=b.id
left join loan d on d.userid=b.id
left join repaydetail e on e.loanid=d.id
where a.id=#{blackid} and e.isoverdue='1'

  and a.userid=#{userid}


  order by ${orderby}


  limit ${beginRow},${pageSize}
