mapper.select:
select

from loanorder
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and orderno = #{orderno,jdbcType=VARCHAR}


  and orderstate = #{orderstate,jdbcType=VARCHAR}



  limit ${beginRow},${pageSize}

select
count(id) as total
from loanorder
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and orderno = #{orderno,jdbcType=VARCHAR}


  and orderstate = #{orderstate,jdbcType=VARCHAR}

mapper.insert:

insert into loanorder (id, orderno, orderqrimage,
  orderstate, paydate, createdate,
  orderdesc, ordertitle, totalamount,
  remark
  )
values (#{id,jdbcType=VARCHAR}, #{orderno,jdbcType=VARCHAR}, #{orderqrimage,jdbcType=VARCHAR},
  #{orderstate,jdbcType=CHAR}, #{paydate,jdbcType=TIMESTAMP}, #{createdate,jdbcType=TIMESTAMP},
  #{orderdesc,jdbcType=VARCHAR}, #{ordertitle,jdbcType=VARCHAR}, #{totalamount,jdbcType=DECIMAL},
  #{remark,jdbcType=VARCHAR}
  )
